import React, {Component} from 'react'
import '../App.css'
// import Dashboard from './dashboard';
// // import Dashboard from './dashboard';

export default class LoginPage extends Component{
    constructor(props){
        super(props)
        this.state = {
            username: '',
            password: '',
            
        }
    }
    getName = e => {
        let username = e.target.value;
        this.setState({
            username: username
        })
    }
    getPassword = e => {
        let password = e.target.value;
        if(password.match('P@SSW0RD')){
            this.setState({
                password: password
             });
        } else {
            console.log("Error");
        }
    }

    handleSubmit = e =>{
        if(!this.state.password === "P@SSW0RD"){
            const user ={
                Username: this.state.username
            }
            this.props.history.push("/dashboard",user)
        }
    }

    
    render(){
        return(
        <div className="login-box">
        <h2>Login</h2>
        <form onSubmit={this.handleSubmit}>
            <div class="user-box"> 
                <input 
                type="text" 
                className="username" 
                id="username" 
                required
                onChange={this.getName}
                ></input>
                <label for="username">Username</label>
            </div>
            <div class="user-box">
                <input 
                type="password" 
                className="password" 
                id="password" 
                required
                onChange={this.getPassword}
                >
                </input>
                <label for="password">Password</label>
            </div>
            <button type="submit">
            <span></span>
            <span></span>
            <span></span>
            <span></span>
            Submit
            </button>
        </form>
    </div>
    )
    }
}
