import React, {Component, useState} from 'react'
import '../App.css'
// import Dashboard from './dashboard';
// // import Dashboard from './dashboard';

export default class LoginPage extends Component{
    constructor(props){
        super(props)
        const [input, setInput] = useState({
            username: '',
            password: ''
        })
    }

    handleChange = e => {
        const {target} = e;
        const {name} = target;
        const value = name

        setInput({
            ...input,
            [name]: value
        })
    }

    
    render(){
        return(
        <div className="login-box">
        <h2>Login</h2>
        <form onSubmit={this.handleSubmit}>
            <div class="user-box"> 
                <input 
                type="text" 
                className="username" 
                id="username" 
                required
                onChange={this.getName}
                ></input>
                <label for="username">Username</label>
            </div>
            <div class="user-box">
                <input 
                type="password" 
                className="password" 
                id="password" 
                required
                onChange={this.getPassword}
                >
                </input>
                <label for="password">Password</label>
            </div>
            <button type="submit">
            <span></span>
            <span></span>
            <span></span>
            <span></span>
            Submit
            </button>
        </form>
    </div>
    )
    }
}
