import React, {Component} from 'react'
import '../App.css'
import { Route, Switch } from 'react-router-dom'
import Dashboard from './dashboard'
// import Dashboard from './dashboard';
// // import Dashboard from './dashboard';

export default class LoginPage extends Component{
    constructor(props){
        super(props)
        this.state = {
            username: '',
            password:''
        }
    }

    onUsernameChange = (username) =>{
        this.setState({
            username: username
        })
    }
    onPasswordChange = (password) =>{
        this.setState({
            password: password
        })
    }
    onSubmit = () =>{
        this.props.onUsernameChange(this.state.username)
    
    }
    render(){
        return(
        <div className="login-box">
        <h2>Login</h2>
        <form 
        onSubmit={this.onSubmit}
        >
            <div class="user-box"> 
                <input 
                type="text" 
                className="username" 
                id="username" 
                required
                onChange={this.onUsernameChange}
                ></input>
                <label for="username">Username</label>
            </div>
            <div class="user-box">
                <input 
                type="password" 
                className="password" 
                id="password" 
                required
                onChange={this.onPasswordChange}
                >
                </input>
                <label for="password">Password</label>
            </div>
            <button type="submit">
            <span></span>
            <span></span>
            <span></span>
            <span></span>
            Submit
            </button>
        </form>
        <Switch>
            <Route path="/dashboard" render={() =><Dashboard username={this.state.username}/>}></Route>
        </Switch>
    </div>
    )
    }
}
