import React, {Component} from 'react'
import './App.css';

export default class Dashboard extends Component{
    constructor(props){
        super(props)
        this.state = {
            username: this.props.username
        }
    }

    // const [username, setUsername] = useState('');
    // const [password, setPassword] = useState('');

    // const onSubmit = e => {
    //         e.preventDefault()
    //         const payload ={
    //         username,
    //         password
    //     }
    //     if(!payload.password.match('P@SSW0RD')){
    //     //
    //     }
    // }

    componentDidMount(){
        let username = this.state.username
        this.props.onShowUsername(username)
    }
    render(){
        return(
            <div className="login-box">
            <h2>Login</h2>
        <form 
        //onSubmit={onSubmit}
        >
            <div class="user-box"> 
                <input 
                type="text" 
                className="username" 
                id="username" 
                required
                //onChange={e => setUsername(e.target.value)}
                ></input>
                    <label for="username">Username</label>
            </div>
            <div class="user-box">
                <input 
                type="password" 
                className="password" 
                id="password" 
                required
                //onChange={e => setPassword(e.target.value)}
                >
                </input>
                <label for="password">Password</label>
            </div>
            <button type="submit">
            <span></span>
            <span></span>
            <span></span>
            <span></span>
            Submit
            </button>
        </form>   
    </div>
        )
    }
}
