import {Component} from 'react'

export default class Dashboard extends Component{

    constructor(props){
        super(props)
        this.state = {
            username: ''
        }
    }
    onShowUsername(username){
        this.setState({username:username})
    }
    render(){
        const {username} = this.state
        return(
            
                {username}
        
            
        )
    }
}
