import React, {Component} from 'react'
import '../App.css'
// import Dashboard from './dashboard';
// // import Dashboard from './dashboard';

export default class LoginPage extends Component{
    constructor(props){
        super(props)
        this.state = {
            username: '',
            password: '',
            payload: ''
        }
    }
    handleSubmit(event){
        let payload = {
            username : this.state.username,
            password: this.state.password
        }
        this.props.history.push('/dashboard',payload)
        console.log(payload)
    }

    
    render(){
        return(
        <div className="login-box">
        <h2>Login</h2>
        <form onSubmit={(event) => this.handleSubmit(event)}>
            <div class="user-box"> 
                <input 
                type="text" 
                className="username" 
                id="username" 
                required
                onChange={(event,newValue) => this.setState({username:newValue})}
                ></input>
                <label for="username">Username</label>
            </div>
            <div class="user-box">
                <input 
                type="password" 
                className="password" 
                id="password" 
                required
                onChange={(event,newValue) => this.setState({password:newValue})}
                >
                </input>
                <label for="password">Password</label>
            </div>
            <button type="submit">
            <span></span>
            <span></span>
            <span></span>
            <span></span>
            Submit
            </button>
        </form>
    </div>
    )
    }
}
